export const filterInitialState = () => {
  const savedFilter = localStorage.getItem('filter');
  if (savedFilter !== null) {
    return JSON.parse(savedFilter);
  }
  return '';
};

export const contactsFilter = (contacts, filters) => {
  console.log(contacts);
  console.log(filters);
  if (!contacts || !filters) {
    return true;
  } else {
    return contacts.items.filter(item => {
      const hasContacts = item.name
        .toLowerCase()
        .includes(filters.toLowerCase());
      if (filters === '') {
        return true;
      }
      return hasContacts;
    });
  }

  // return contacts;
};
