import { createSlice } from '@reduxjs/toolkit';
// import { contactsInitialState } from './functions';
import { fetchContacts, addContact, deleteContact } from './operations';
import { useSelector } from 'react-redux';
import { selectContacts, selectVisibleContacts } from './selectors';

const handlePending = state => {
  state.isLoading = true;
};

const handleRejected = (state, action) => {
  state.isLoading = false;
  state.error = action.payload;
};

const contactsSlice = createSlice({
  name: 'contacts',
  // initialState: contactsInitialState,
  initialState: {
    items: [],
    isLoading: false,
    error: null,
  },
  extraReducers: {
    [fetchContacts.pending]: handlePending,
    [fetchContacts.fulfilled](state, action) {
      state.isLoading = false;
      state.error = null;
      state.items = action.payload;
      console.log(state.items);
    },
    [fetchContacts.rejected]: handleRejected,

    [addContact.pending]: handlePending,
    [addContact.fulfilled](state, action) {
      state.isLoading = false;
      state.error = null;
      state.items = action.payload;
      // state.push({ ...action.payload, id: nanoid() });
      // localStorage.setItem('setContacts', JSON.stringify(state));
    },
    [addContact.rejected]: handleRejected,

    [deleteContact.pending](state, action) {
      state.isLoading = true;
    },
    [deleteContact.fulfilled](state, action) {
      // const contactsList = useSelector(state => state.contacts);
      state.isLoading = false;
      state.error = null;
      console.log(state.items());

      console.log(state);
      state.items = action.payload;
      console.log(action.payload);
      console.log(state.items);
      // console.log(contactsList);
      // console.log(selectVisibleContacts(state));

      const index = state.items.findIndex(
        contact => contact.id === action.payload
      );
      state.splice(index, 1);
      //   localStorage.setItem('setContacts', JSON.stringify(state));
    },
    [deleteContact.rejected]: handleRejected,
  },
});
export const contactsReducer = contactsSlice.reducer;
// export const { fetchContacts, addContact, deleteContact } =
//   contactsSlice.actions;
